import React, { useEffect, useState } from 'react';
import axios from './api';

const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:3000';

export default function LogList({ token }) {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);

  useEffect(() => { fetchLogs(page); }, [page]);

  async function fetchLogs(p = 1) {
    setLoading(true);
    try {
      const res = await axios.get(`${API_BASE}/logs?limit=100&page=${p}`);
      setLogs(res.data.items || []);
    } catch (err) {
      console.error('Fetch logs error', err);
      setLogs([]);
    } finally { setLoading(false); }
  }

  return (
    <div style={{minWidth:600}}>
      <h3>Activity logs</h3>
      {loading ? <div>Đang tải...</div> : (
        <div style={{maxHeight:420, overflow:'auto'}}>
          <table className="table">
            <thead>
              <tr><th>Thời gian</th><th>Người dùng</th><th>Hành động</th><th>IP</th><th>Meta</th></tr>
            </thead>
            <tbody>
              {logs.map(l => (
                <tr key={l._id}>
                  <td style={{whiteSpace:'nowrap'}}>{new Date(l.timestamp).toLocaleString()}</td>
                  <td>{l.userId || '-'}</td>
                  <td>{l.action}</td>
                  <td>{l.ip || '-'}</td>
                  <td style={{maxWidth:300, overflow:'hidden', textOverflow:'ellipsis'}}>{l.meta ? JSON.stringify(l.meta) : ''}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
      <div style={{display:'flex',gap:8,justifyContent:'flex-end',marginTop:8}}>
        <button className="btn small" onClick={() => setPage(p => Math.max(1, p-1))}>Previous</button>
        <button className="btn small" onClick={() => setPage(p => p+1)}>Next</button>
      </div>
    </div>
  );
}
